import{_ as n,c as o,o as e,h as t}from"./app.e4480542.js";const _=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"blogs/css/componnet-token-cssvar.md","lastUpdated":1720104467000}'),p={name:"blogs/css/componnet-token-cssvar.md"},s=t("<p>antd\u7684Component Token\u662F\u4E00\u4E2A\u4EC0\u4E48\u6982\u5FF5</p><p>Component Token \u662F\u5173\u4E8E\u7EC4\u4EF6\u6837\u5F0F\u7684\u4E00\u79CD\u6982\u5FF5\u3002</p><p>\u65B9\u4FBF\u7406\u89E3\u7684\u8BDD\uFF0C\u4F60\u53EF\u4EE5\u628A Component Token \u770B\u505A\u662F\u7EC4\u4EF6\u4E2D\u7684\u6837\u5F0F\u53D8\u91CF\u3002\u6BD4\u5982\u8BF4\uFF0C\u4E00\u4E2A\u6309\u94AE\u7EC4\u4EF6\u7684\u989C\u8272\u3001\u5927\u5C0F\u3001\u8FB9\u8DDD\u90FD\u53EF\u4EE5\u88AB\u5B9A\u4E49\u6210\u4E00\u4E2A\u4E2A Token\uFF0C\u800C\u8FD9\u4E9B Token \u53EF\u4EE5\u88AB\u591A\u6B21\u7684\u590D\u7528\u5E76\u53EF\u4EE5\u975E\u5E38\u65B9\u4FBF\u7684\u66F4\u65B0\u3002</p><p>\u8FD9\u79CD\u65B9\u5F0F\u7684\u597D\u5904\u662F\u53EF\u4EE5\u4F7F\u8BBE\u8BA1\u548C\u5F00\u53D1\u4EBA\u5458\u66F4\u597D\u5730\u5171\u4EAB\u7EC4\u4EF6\u5E93\u4E2D\u7684\u8BBE\u8BA1\u5C5E\u6027\uFF0C\u540C\u65F6\u8FD8\u53EF\u4EE5\u9002\u5E94\u5404\u7C7B\u4E3B\u9898\u7684\u66F4\u6539\u3002\u6BD4\u5982\u8BF4\uFF0C\u4F60\u53EA\u9700\u8981\u4FEE\u6539\u5BF9\u5E94\u7684 Token \u503C\uFF0C\u5C31\u80FD\u5728\u5168\u7AD9\u8303\u56F4\u5185\u6539\u53D8\u6309\u94AE\u7684\u989C\u8272\u6216\u5927\u5C0F\uFF0C\u800C\u65E0\u9700\u9010\u4E2A\u4FEE\u6539\u6BCF\u4E2A\u4F7F\u7528\u4E86\u6B64\u6309\u94AE\u7684\u5730\u65B9\u3002</p><p>\u5728 Antd \u4E2D\uFF0C\u63D0\u4F9B\u4E86\u4E00\u5957\u9F50\u5168\u7684\u9ED8\u8BA4 Token\uFF0C\u5F00\u53D1\u8005\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u9700\u8981\u4ECE\u4E2D\u9009\u62E9\uFF0C\u6216\u8005\u6839\u636E\u9879\u76EE\u7684\u5B9E\u9645\u9700\u6C42\u53BB\u81EA\u5B9A\u4E49\u65B0\u7684 Token\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0CComponent Token \u5C31\u662F\u4E3A\u4E86\u7B80\u5316\u7EC4\u4EF6\u6837\u5F0F\u7BA1\u7406\u548C\u4E3B\u9898\u5B9A\u5236\u800C\u8BBE\u8BA1\u7684\u4E00\u79CD\u673A\u5236\u3002</p><p>\u90A3\u8FD9\u4E0D\u5C31\u662Fcss\u53D8\u91CF\u5417</p><p>\u662F\u7684\uFF0CComponent Token \u7684\u6982\u5FF5\u548C CSS \u53D8\u91CF\u786E\u5B9E\u975E\u5E38\u76F8\u4F3C\uFF0C\u5B83\u4EEC\u90FD\u662F\u4E00\u79CD\u6837\u5F0F\u53D8\u91CF\uFF0C\u7528\u4E8E\u5B58\u50A8\u548C\u590D\u7528\u6837\u5F0F\u4FE1\u606F\u3002\u4ECE\u672C\u8D28\u4E0A\u6765\u8BB2\uFF0CComponent Token \u5B9E\u9645\u4E0A\u5C31\u662F\u4E00\u79CD\u9AD8\u7EA7\u5F62\u5F0F\u7684 CSS \u53D8\u91CF\uFF0C\u5177\u6709\u66F4\u9AD8\u7684\u7ED3\u6784\u5316\u548C\u8BED\u4E49\u5316\u3002</p><p>\u4E0D\u8FC7\uFF0CComponent Token \u5728\u4F7F\u7528\u4E0A\u6709\u4E00\u4E9B\u533A\u522B\u548C\u4F18\u52BF\uFF1A</p><ol><li>**\u7EC4\u5408\u6027\u3002**Component Token \u53EF\u4EE5\u7EC4\u5408\u4F7F\u7528\uFF0C\u4F7F\u5F97\u4F60\u5275\u5EFA\u7684\u9AD4\u7CFB\u66F4\u4E3A\u6A21\u5757\u5316\u548C\u53EF\u7EF4\u62A4\u3002\u4F8B\u5982\uFF0C\u4F60\u53EF\u4EE5\u6709\u4E00\u4E2A <code>primary-color</code> \u7684 Token\uFF0C\u7528\u5B83\u6765\u5B9A\u4E49\u6309\u94AE\u7684\u80CC\u666F\u8272\uFF0C\u540C\u6642\u9019\u500B Token \u53EF\u4EE5\u5728\u4E0D\u540C\u7EC4\u4EF6\u6216\u8005\u5143\u7D20\u4E2D\u5171\u4EAB\uFF0C\u5982\u94FE\u63A5\u3001\u6807\u9898\u7B49\u3002</li><li>**\u4E3B\u9898\u5B9A\u5236\u3002**\u901A\u8FC7\u6539\u53D8 Component Tokens \u7684\u503C\uFF0C\u4F60\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u521B\u5EFA\u5B8C\u5168\u4E0D\u540C\u7684\u4E3B\u9898\uFF0C\u4F8B\u5982\u6DF1\u8272\u4E3B\u9898\u3001\u6D45\u8272\u4E3B\u9898\u7B49\u3002</li><li>**\u7F16\u7A0B\u7075\u6D3B\u6027\u3002**\u76F8\u6BD4\u7EAF CSS \u53D8\u91CF\uFF0CComponent Token \u65B9\u6848\u53EF\u4EE5\u914D\u5408 JavaScript \u6216 TypeScript \u7684\u53D8\u91CF\u548C\u903B\u8F91\uFF0C\u63D0\u4F9B\u66F4\u5F3A\u5927\u7684\u7075\u6D3B\u6027\u3002</li></ol><p>\u4F46\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0CAnt Design \u7684 Component Token \u662F\u5EFA\u7ACB\u5728 Less/Sass \u8FD9\u7C7B\u9884\u5904\u7406\u5668\u4E0A\uFF0C\u800C\u4E0D\u662F\u539F\u751F CSS\uFF0C\u5B83\u662F\u901A\u8FC7\u9884\u5904\u7406\u5668\u7684\u53D8\u91CF\u5B9E\u73B0\u7684\uFF0C\u57FA\u4E8E build \u5DE5\u5177\u53BB\u5B9E\u73B0\u7684\u7F16\u8BD1\u65F6\u4FEE\u6539\uFF0C\u6240\u4EE5\u5728\u4F7F\u7528\u4E0A\u4F1A\u6BD4\u539F\u751F CSS \u53D8\u91CF\u590D\u6742\u4E00\u4E9B\uFF0C\u9700\u8981\u8BBE\u7F6E\u7F16\u8BD1\u73AF\u5883\u548C\u5DE5\u5177\u94FE\u3002</p><p>Component Token \u548C CSS \u53D8\u91CF\u7684\u4E3B\u8981\u533A\u522B\u662F\u4EC0\u4E48\uFF1F</p><p>\u5728 Ant Design \u7684 Component Token \u548C CSS \u53D8\u91CF\u4E4B\u95F4\uFF0C\u867D\u7136\u5B83\u4EEC\u90FD\u88AB\u7528\u4F5C\u5B58\u50A8\u53EF\u91CD\u7528\u7684\u6837\u5F0F\u4FE1\u606F\uFF0C\u4F46\u5B83\u4EEC\u6709\u4E00\u4E9B\u5173\u952E\u7684\u533A\u522B\uFF1A 1. <strong>\u9002\u7528\u8303\u56F4</strong>\uFF1ACSS \u53D8\u91CF\u662F Web \u6807\u51C6\uFF0C\u80FD\u591F\u5728\u4EFB\u4F55\u652F\u6301\u7684\u6D4F\u89C8\u5668\u4E2D\u4F7F\u7528\u3002\u800C Ant Design \u7684 Component Token \u662F\u7279\u5B9A\u4E8E\u8FD9\u4E2A\u6846\u67B6\u7684\uFF0C\u53EA\u80FD\u5728\u4F7F\u7528 Ant Design\u65F6\u4F7F\u7528\u3002 2. <strong>\u4F7F\u7528\u65B9\u5F0F</strong>\uFF1ACSS \u53D8\u91CF\u901A\u8FC7 CSS \u81EA\u5B9A\u4E49\u5C5E\u6027\u5728 CSS \u6587\u4EF6\u4E2D\u5B9A\u4E49\u548C\u4F7F\u7528\u3002\u800C Component Token \u662F\u5728 Ant Design \u7684\u6837\u5F0F\u914D\u7F6E\u6587\u4EF6\u4E2D\u5B9A\u4E49\u7684\uFF0C\u5728 JS \u6587\u4EF6\u4E2D\u901A\u8FC7\u5F15\u7528\u6765\u4F7F\u7528\uFF0C\u7136\u540E\u5728\u7F16\u8BD1\u9636\u6BB5\u66FF\u6362\u4E3A\u5B9E\u9645\u7684\u6837\u5F0F\u503C\u3002 3. <strong>\u4E3B\u9898\u652F\u6301</strong>\uFF1A\u867D\u7136 CSS \u53D8\u91CF\u53EF\u4EE5\u7528\u6765\u5B9E\u73B0\u4E3B\u9898\u5207\u6362\uFF0C\u4F46\u4E3B\u9898\u5207\u6362\u9700\u8981\u901A\u8FC7 JavaScript\u6765\u52A8\u6001\u6539\u53D8\u53D8\u91CF\u7684\u503C\u3002\u800C Ant Design \u7684 Component Token \u53EF\u4EE5\u5728\u7F16\u8BD1\u65F6\u6839\u636E\u914D\u7F6E\u751F\u6210\u4E0D\u540C\u7684\u4E3B\u9898\u6837\u5F0F\u3002 4. <strong>\u7075\u6D3B\u6027</strong>\uFF1AAnt Design \u7684 Component Token \u652F\u6301\u66F4\u591A\u7684\u6570\u636E\u7C7B\u578B\u548C\u66F4\u590D\u6742\u7684\u503C\uFF0C\u4F8B\u5982\u5C3A\u5BF8\u3001\u989C\u8272\u3001\u5B57\u4F53\u7B49\u53EF\u4EE5\u88AB\u5B9A\u4E49\u4E3A\u4E00\u4E2A Token\u3002\u800C CSS \u53D8\u91CF\u53EA\u652F\u6301\u5B57\u7B26\u4E32\u503C\u3002 5. <strong>\u4F5C\u7528\u8303\u56F4</strong>\uFF1ACSS \u53D8\u91CF\u6709\u4F5C\u7528\u8303\u56F4\u7684\u6982\u5FF5\uFF0C\u53EF\u4EE5\u5728\u5168\u5C40\u6216\u7279\u5B9A\u5143\u7D20\u4E2D\u5B9A\u4E49\u3002\u800C Component Token \u662F\u5168\u5C40\u7684\uFF0C\u53EF\u4EE5\u88AB\u4EFB\u4F55 Ant Design \u7EC4\u4EF6\u5F15\u7528\u3002 6. <strong>\u517C\u5BB9\u6027</strong>\uFF1ACSS \u53D8\u91CF\u7684\u6D4F\u89C8\u5668\u517C\u5BB9\u6027\u597D\uFF0C\u800C Component Token \u56E0\u4E3A\u9700\u8981\u7279\u5B9A\u7684\u6784\u5EFA\u5DE5\u5177\u548C\u73AF\u5883\uFF0C\u6240\u4EE5\u517C\u5BB9\u6027\u53D7\u5236\u4E8E\u8FD9\u4E9B\u5DE5\u5177\u548C\u73AF\u5883\u3002 \u603B\u7684\u6765\u8BF4\uFF0C\u4E24\u8005\u7684\u4E3B\u8981\u533A\u522B\u5728\u4E8E\u4F7F\u7528\u65B9\u5F0F\u3001\u9002\u7528\u8303\u56F4\u3001\u7075\u6D3B\u6027\u4EE5\u53CA\u517C\u5BB9\u6027\u7B49\u65B9\u9762\u3002</p>",13),r=[s];function S(C,T,a,c,k,i){return e(),o("div",null,r)}const g=n(p,[["render",S]]);export{_ as __pageData,g as default};
